INSERT INTO `documents` VALUES (1,2,2,'Definción funcional del firmador digital','Adjuntamos los requerimientos básicos para el desarrollo del firmador digital de escritorio.\r\nTDR Términos de Referencia ','2012-12-03 16:49:38'),(3,48,1,'Servicio de Información Universitario SIU','El SIU es un consorcio de 46 Universidades Públicas y está  financiado por la Secretaria de Políticas Universitarias del Ministerio de Educación de la Nación. ','2012-12-03 20:27:50'),(4,5,1,'Firmador Digital','Referencia para el desarrollo de un firmador digital para PCs escritorios desktop y laptop y dispositivos móviles con Android.\r\n','2012-12-03 20:32:12'),(5,7,2,'Cciclo de vida de los certificados PKI','La creciente demanda de implementación de procesos digitales (digitalización) en la Administración Pública Nacional, conlleva la necesidad de contar con una plataforma ágil e inteligente para la gestión de Certificados Digitales en la tramitación de la Firma Digital.\r\n\r\nEsta plataforma basada en software se encarga de administrar el ciclo de vida de los certificados digitales administrada por la ONTI.\r\n\r\nSe adjunta el documento qeu define los requerimientos a estos efectos.\r\n','2012-12-03 20:45:44'),(6,49,1,'Red digital del patrimonio cultural ','Proyecto de un Portal de la cultura patrimonial de la Provincia de Buenos Aires.\r\n','2012-12-03 21:00:38'),(7,50,1,'Hospital digital','El Cruce una experiencia en la implementación de un hospital digital publico.','2012-12-03 21:13:51'),(8,50,2,'Pliego técnico hospital digital','DIGITALIZACIÓN, ARCHIVO Y DISTRIBUCIÓN DE LAS IMÁGENES\r\nPRODUCIDAS POR EL SERVICIO DE DIAGNÓSTICO POR IMÁGENES DEL\r\nHOSPITAL “EL CRUCE” DE FLORENCIO VARELA','2012-12-03 21:17:15'),(9,51,1,'SIARH - Ideas fuerza','10 ideas fuerza del Sistema Integrado par la Administración de Recursos Humanos','2012-12-03 21:28:49'),(10,51,1,'SIARH ','El SIARH es un sistema para la gestión de recursos humanos. Esta compuesto por dos módulos que se integran operativamente: el correspondiente a  las tramitaciones que hacen a  Gestión del Personal y los procesos que corresponden a  Liquidación de Haberes.','2012-12-03 21:29:20'),(11,10,1,'SIPRO','Documento base sobre los requerimientos funcionales del Sistema de Proveedores','2012-12-03 21:36:26'),(12,52,1,'Sistema de bienes y servicios - SIBYS -','Descripción funcional de un Sistema para la catalogación de Bienes y Servicios.','2012-12-03 21:38:36'),(13,53,1,'PAC','Descripción funcional para un Sistema de Plan Anual de Contrataciones.','2012-12-03 21:42:38'),(14,54,1,'Comunas Digitales','15 Comunas porteñas 15 centros para una ciudadanía digital','2012-12-03 22:04:17'),(15,8,2,'Arquitectura del Sistema Ar-compra','Documento que describe la arquitectura del Ar-compra','2012-12-03 22:12:05'),(16,8,1,'Etapa de finalziación','Documento que describe el proceso de \"Etapa de finalización\" que permite producir los requerimientos funcionales de este componente y los casos de uso.\r\n','2012-12-03 22:15:09'),(17,36,1,'Política de Certificación AC ONTI y Resolución aprobatoria','Política de Certificación de la Autoridad Certificante de la ONTI y Resolución aprobatoria','2012-12-05 09:50:24'),(18,36,1,'Manual de Procedimientos AC ONTI','','2012-12-05 09:53:10'),(19,35,1,'Notas de Conformacion de Autoridad de Registro (APN)','','2012-12-05 11:52:32'),(20,35,1,'NOTAS Tipo para conformar la AR en el Sector Público','','2012-12-05 11:56:14'),(21,35,1,'Nota tipo asignacion de dominios','Una vez conformada la Autoridad de Registro, debe informar los dominios de correo a fin de poder utilizar la Aplicacion de la Autoridad Certificante.','2012-12-05 11:59:14'),(23,35,1,'Pasos conformación AR','','2012-12-07 13:59:12');
